/**
 * ProposalList 컴포넌트
 * 제안글 목록을 렌더링하는 컴포넌트
 *
 * @param {Array} proposalList - 제안글 목록 데이터
 * @param {Function} onLike - 좋아요 토글 함수
 * @param {Function} onScrap - 스크랩 토글 함수
 */
const ProposalList = ({ proposalList = [], onLike, onScrap }) => {
  const handleLikeClick = (proposalId, isLiked) => {
    if (onLike) {
      onLike(proposalId, isLiked);
    }
  };

  const handleScrapClick = (proposalId, isScraped) => {
    if (onScrap) {
      onScrap(proposalId, isScraped);
    }
  };

  return (
    <SLayout>
      {proposalList.map((proposal) => (
        <SProposalCard key={proposal.id}>
          <SBadgeWrapper>
            <Badge>{proposal.label}</Badge>
          </SBadgeWrapper>

          <STitleWrapper>
            <STitle>{proposal.title}</STitle>
          </STitleWrapper>

          <SDescriptionWrapper>
            <SDescription>{proposal.description}</SDescription>
          </SDescriptionWrapper>

          <SDataListWrapper>
            <DataList.Root orientation='horizontal'>
              <SCustomItem>
                <SCustomLabel>희망시간</SCustomLabel>
                <SCustomValue>
                  {proposal.timeInput} - {proposal.timeInputEnd}
                </SCustomValue>
              </SCustomItem>
              <SCustomItem>
                <SCustomLabel>희망장소</SCustomLabel>
                <SCustomValue>
                  {proposal.locationInput} + {proposal.additionalText}
                </SCustomValue>
              </SCustomItem>
            </DataList.Root>
          </SDataListWrapper>

          <SImageCarouselWrapper>
            <Carousel gap='0.5rem'>
              {Array.from({ length: 3 }, (_, index) => {
                const hasImage =
                  proposal.imageList && proposal.imageList[index];
                return (
                  <SImageItem key={index}>
                    <SImage
                      src={
                        hasImage ? proposal.imageList[index] : DefaultImageSrc
                      }
                      alt={
                        hasImage ? `제안글 이미지 ${index + 1}` : '기본 이미지'
                      }
                    />
                  </SImageItem>
                );
              })}
            </Carousel>
          </SImageCarouselWrapper>

          <SFooterWrapper>
            <SUserInfoContainer>
              <Avatar.Root size='xs'>
                <Avatar.Fallback name={proposal.authorName} />
              </Avatar.Root>
              {proposal.authorName}
              <SSeparator>|</SSeparator>
              <STimeAgo>{proposal.timeAgo}</STimeAgo>
            </SUserInfoContainer>

            <SActionContainer>
              <SActionBtn
                onClick={() => handleLikeClick(proposal.id, proposal.isLiked)}
              >
                {proposal.isLiked ? <DisabledHeart /> : <DefaultHeart />}
                <SActionCount $isActive={proposal.isLiked}>
                  {proposal.likeCount}
                </SActionCount>
              </SActionBtn>

              <SActionBtn
                onClick={() =>
                  handleScrapClick(proposal.id, proposal.isScraped)
                }
              >
                {proposal.isScraped ? <DisabledScrap /> : <DefaultScrap />}
                <SActionCount $isActive={proposal.isScraped}>
                  {proposal.scrapCount}
                </SActionCount>
              </SActionBtn>
            </SActionContainer>
          </SFooterWrapper>
        </SProposalCard>
      ))}
    </SLayout>
  );
};

